mapper.update:
UPDATE board SET
b_hit=b_hit+1
WHERE b_cate_no=#{b_cate_no} AND b_no=#{b_no}

mapper.select:
SELECT b_no,b_cate_no,member_no,b_subject,b_content,b_regdate,b_hit
FROM board
WHERE b_cate_no=#{b_cate_no} AND b_no=#{b_no}

mapper.update:
UPDATE board SET
b_subject=#{b_subject},
b_content=#{b_content}
WHERE b_no=#{b_no}

UPDATE community_reply_board SET
r_content=#{r_content}
WHERE r_no=#{r_no}
